  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import express, { type Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertDriverSchema, insertVehicleSchema, insertLicenseSchema, insertOrderSchema } from "@shared/schema";
import multer from "multer";
import path from "path";

// Configure multer for photo uploads
const upload = multer({
  dest: 'uploads/',
  limits: {
    fileSize: 5 * 1024 * 1024, // 5MB limit
  },
  fileFilter: (req, file, cb) => {
    if (file.mimetype.startsWith('image/')) {
      cb(null, true);
    } else {
      cb(new Error('Only image files are allowed'));
    }
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Driver routes
  app.get("/api/drivers/:id", async (req, res) => {
    try {
      const driver = await storage.getDriver(req.params.id);
      if (!driver) {
        return res.status(404).json({ message: "Driver not found" });
      }
      res.json(driver);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.get("/api/drivers/:id/profile", async (req, res) => {
    try {
      const profile = await storage.getDriverProfile(req.params.id);
      if (!profile) {
        return res.status(404).json({ message: "Driver profile not found" });
      }
      res.json(profile);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get driver profile by email
  app.get("/api/drivers-by-email/:email/profile", async (req, res) => {
    try {
      const driver = await storage.getDriverByEmail(req.params.email);
      if (!driver) {
        return res.status(404).json({ message: "Driver not found" });
      }
      const profile = await storage.getDriverProfile(driver.id);
      if (!profile) {
        return res.status(404).json({ message: "Driver profile not found" });
      }
      res.json(profile);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get driver orders by email
  app.get("/api/drivers-by-email/:email/orders", async (req, res) => {
    try {
      const driver = await storage.getDriverByEmail(req.params.email);
      if (!driver) {
        return res.status(404).json({ message: "Driver not found" });
      }
      const orders = await storage.getOrdersByDriverId(driver.id);
      res.json(orders);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get driver earnings by email
  app.get("/api/drivers-by-email/:email/earnings/:period", async (req, res) => {
    try {
      const driver = await storage.getDriverByEmail(req.params.email);
      if (!driver) {
        return res.status(404).json({ message: "Driver not found" });
      }

      const { period } = req.params;
      let earnings;

      switch (period) {
        case "weekly":
          earnings = await storage.getWeeklyEarnings(driver.id);
          break;
        case "monthly":
          // For now, return all earnings (could implement monthly filter later)
          earnings = await storage.getEarningsByDriverId(driver.id);
          break;
        default:
          earnings = await storage.getEarningsByDriverId(driver.id);
      }

      res.json(earnings);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/drivers", async (req, res) => {
    try {
      const validatedData = insertDriverSchema.parse(req.body);
      const driver = await storage.createDriver(validatedData);
      res.status(201).json(driver);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.patch("/api/drivers/:id", async (req, res) => {
    try {
      const driver = await storage.updateDriver(req.params.id, req.body);
      res.json(driver);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Vehicle routes
  app.get("/api/drivers/:driverId/vehicle", async (req, res) => {
    try {
      const vehicle = await storage.getVehicleByDriverId(req.params.driverId);
      if (!vehicle) {
        return res.status(404).json({ message: "Vehicle not found" });
      }
      res.json(vehicle);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/vehicles", async (req, res) => {
    try {
      const validatedData = insertVehicleSchema.parse(req.body);
      const vehicle = await storage.createVehicle(validatedData);
      res.status(201).json(vehicle);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // License routes
  app.get("/api/drivers/:driverId/license", async (req, res) => {
    try {
      const license = await storage.getLicenseByDriverId(req.params.driverId);
      if (!license) {
        return res.status(404).json({ message: "License not found" });
      }
      res.json(license);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/licenses", async (req, res) => {
    try {
      const validatedData = insertLicenseSchema.parse(req.body);
      const license = await storage.createLicense(validatedData);
      res.status(201).json(license);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Order routes
  app.get("/api/orders/pending", async (req, res) => {
    try {
      const orders = await storage.getPendingOrders();
      res.json(orders);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.get("/api/drivers/:driverId/orders", async (req, res) => {
    try {
      const orders = await storage.getOrdersByDriverId(req.params.driverId);
      res.json(orders);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/orders", async (req, res) => {
    try {
      const validatedData = insertOrderSchema.parse(req.body);
      const order = await storage.createOrder(validatedData);
      res.status(201).json(order);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.patch("/api/orders/:id", async (req, res) => {
    try {
      const order = await storage.updateOrder(req.params.id, req.body);
      res.json(order);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/orders/:id/accept", async (req, res) => {
    try {
      const { driverId } = req.body;
      if (!driverId) {
        return res.status(400).json({ message: "Driver ID is required" });
      }
      const order = await storage.acceptOrder(req.params.id, driverId);
      res.json(order);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/orders/:id/upload-photo", upload.single('photo'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No photo uploaded" });
      }

      const photoUrl = `/uploads/${req.file.filename}`;
      const order = await storage.updateOrder(req.params.id, { photoUrl });
      res.json(order);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Earnings routes
  app.get("/api/drivers/:driverId/earnings", async (req, res) => {
    try {
      const earnings = await storage.getEarningsByDriverId(req.params.driverId);
      res.json(earnings);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.get("/api/drivers/:driverId/earnings/daily/:date", async (req, res) => {
    try {
      const earnings = await storage.getDailyEarnings(req.params.driverId, req.params.date);
      res.json(earnings);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.get("/api/drivers/:driverId/earnings/weekly", async (req, res) => {
    try {
      const earnings = await storage.getWeeklyEarnings(req.params.driverId);
      res.json(earnings);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Serve uploaded files
  app.use('/uploads', express.static('uploads'));

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type Driver, type InsertDriver, type Vehicle, type InsertVehicle, type License, type InsertLicense, type Order, type InsertOrder, type Earning, type InsertEarning, type DriverProfile, type OrderWithEarnings } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Driver operations
  getDriver(id: string): Promise<Driver | undefined>;
  getDriverByEmail(email: string): Promise<Driver | undefined>;
  createDriver(driver: InsertDriver): Promise<Driver>;
  updateDriver(id: string, updates: Partial<Driver>): Promise<Driver>;
  getDriverProfile(driverId: string): Promise<DriverProfile | undefined>;

  // Vehicle operations
  getVehicleByDriverId(driverId: string): Promise<Vehicle | undefined>;
  createVehicle(vehicle: InsertVehicle): Promise<Vehicle>;
  updateVehicle(id: string, updates: Partial<Vehicle>): Promise<Vehicle>;

  // License operations
  getLicenseByDriverId(driverId: string): Promise<License | undefined>;
  createLicense(license: InsertLicense): Promise<License>;
  updateLicense(id: string, updates: Partial<License>): Promise<License>;

  // Order operations
  getOrder(id: string): Promise<Order | undefined>;
  getOrdersByDriverId(driverId: string): Promise<OrderWithEarnings[]>;
  getPendingOrders(): Promise<Order[]>;
  createOrder(order: InsertOrder): Promise<Order>;
  updateOrder(id: string, updates: Partial<Order>): Promise<Order>;
  acceptOrder(orderId: string, driverId: string): Promise<Order>;

  // Earnings operations
  getEarningsByDriverId(driverId: string): Promise<Earning[]>;
  createEarning(earning: InsertEarning): Promise<Earning>;
  getDailyEarnings(driverId: string, date: string): Promise<Earning[]>;
  getWeeklyEarnings(driverId: string): Promise<Earning[]>;
}

export class MemStorage implements IStorage {
  private drivers: Map<string, Driver>;
  private vehicles: Map<string, Vehicle>;
  private licenses: Map<string, License>;
  private orders: Map<string, Order>;
  private earnings: Map<string, Earning>;

  constructor() {
    this.drivers = new Map();
    this.vehicles = new Map();
    this.licenses = new Map();
    this.orders = new Map();
    this.earnings = new Map();

    // Initialize with sample driver
    this.initializeSampleData();
  }

  private async initializeSampleData() {
    const driverId = randomUUID();
    const driver: Driver = {
      id: driverId,
      name: "김민수",
      email: "driver@logishare.com",
      phone: "010-1234-5678",
      rating: "4.9",
      totalDeliveries: 328,
      completionRate: "97.8",
      isOnline: true,
      createdAt: new Date(),
    };
    this.drivers.set(driverId, driver);

    const vehicle: Vehicle = {
      id: randomUUID(),
      driverId,
      licensePlate: "서울12가3456",
      model: "현대 포터 II",
      capacity: "1톤",
      insuranceExpiry: "2024.08.15",
    };
    this.vehicles.set(vehicle.id, vehicle);

    const license: License = {
      id: randomUUID(),
      driverId,
      licenseType: "1종 보통",
      licenseNumber: "11-23-456789-00",
      issueDate: "2018.03.15",
      renewalDate: "2025.03.15",
    };
    this.licenses.set(license.id, license);

    // Sample orders
    const orders = [
      {
        id: randomUUID(),
        orderNumber: "LS-2024-0158",
        driverId,
        pickupLocation: "홈플러스 강남점",
        deliveryLocation: "서울시 강남구 역삼동",
        status: "in_transit",
        estimatedTime: 25,
        distance: "8.5",
        fee: "12500",
        photoUrl: null,
        createdAt: new Date(),
        completedAt: null,
      },
      {
        id: randomUUID(),
        orderNumber: "LS-2024-0159",
        driverId: null,
        pickupLocation: "이마트 트레이더스 월드컵점",
        deliveryLocation: "서울시 마포구 상암동",
        status: "pending",
        estimatedTime: 25,
        distance: "8.5",
        fee: "15000",
        photoUrl: null,
        createdAt: new Date(),
        completedAt: null,
      },
      {
        id: randomUUID(),
        orderNumber: "LS-2024-0160",
        driverId: null,
        pickupLocation: "롯데마트 서울역점",
        deliveryLocation: "서울시 용산구 한강로",
        status: "pending",
        estimatedTime: 18,
        distance: "5.2",
        fee: "9500",
        photoUrl: null,
        createdAt: new Date(),
        completedAt: null,
      }
    ];

    orders.forEach(order => this.orders.set(order.id, order as Order));
  }

  async getDriver(id: string): Promise<Driver | undefined> {
    return this.drivers.get(id);
  }

  async getDriverByEmail(email: string): Promise<Driver | undefined> {
    return Array.from(this.drivers.values()).find(driver => driver.email === email);
  }

  async createDriver(insertDriver: InsertDriver): Promise<Driver> {
    const id = randomUUID();
    const driver: Driver = {
      id,
      name: insertDriver.name,
      email: insertDriver.email,
      phone: insertDriver.phone,
      rating: insertDriver.rating || null,
      totalDeliveries: insertDriver.totalDeliveries || null,
      completionRate: insertDriver.completionRate || null,
      isOnline: insertDriver.isOnline || null,
      createdAt: new Date(),
    };
    this.drivers.set(id, driver);
    return driver;
  }

  async updateDriver(id: string, updates: Partial<Driver>): Promise<Driver> {
    const existing = this.drivers.get(id);
    if (!existing) throw new Error("Driver not found");

    const updated = { ...existing, ...updates };
    this.drivers.set(id, updated);
    return updated;
  }

  async getDriverProfile(driverId: string): Promise<DriverProfile | undefined> {
    const driver = await this.getDriver(driverId);
    if (!driver) return undefined;

    const vehicle = await this.getVehicleByDriverId(driverId);
    const license = await this.getLicenseByDriverId(driverId);

    return {
      ...driver,
      vehicle,
      license,
    };
  }

  async getVehicleByDriverId(driverId: string): Promise<Vehicle | undefined> {
    return Array.from(this.vehicles.values()).find(vehicle => vehicle.driverId === driverId);
  }

  async createVehicle(insertVehicle: InsertVehicle): Promise<Vehicle> {
    const id = randomUUID();
    const vehicle: Vehicle = { ...insertVehicle, id };
    this.vehicles.set(id, vehicle);
    return vehicle;
  }

  async updateVehicle(id: string, updates: Partial<Vehicle>): Promise<Vehicle> {
    const existing = this.vehicles.get(id);
    if (!existing) throw new Error("Vehicle not found");

    const updated = { ...existing, ...updates };
    this.vehicles.set(id, updated);
    return updated;
  }

  async getLicenseByDriverId(driverId: string): Promise<License | undefined> {
    return Array.from(this.licenses.values()).find(license => license.driverId === driverId);
  }

  async createLicense(insertLicense: InsertLicense): Promise<License> {
    const id = randomUUID();
    const license: License = { ...insertLicense, id };
    this.licenses.set(id, license);
    return license;
  }

  async updateLicense(id: string, updates: Partial<License>): Promise<License> {
    const existing = this.licenses.get(id);
    if (!existing) throw new Error("License not found");

    const updated = { ...existing, ...updates };
    this.licenses.set(id, updated);
    return updated;
  }

  async getOrder(id: string): Promise<Order | undefined> {
    return this.orders.get(id);
  }

  async getOrdersByDriverId(driverId: string): Promise<OrderWithEarnings[]> {
    const orders = Array.from(this.orders.values()).filter(order => order.driverId === driverId);
    return Promise.all(orders.map(async order => {
      const earning = Array.from(this.earnings.values()).find(e => e.orderId === order.id);
      return { ...order, earnings: earning };
    }));
  }

  async getPendingOrders(): Promise<Order[]> {
    return Array.from(this.orders.values()).filter(order => order.status === "pending");
  }

  async createOrder(insertOrder: InsertOrder): Promise<Order> {
    const id = randomUUID();
    const order: Order = {
      id,
      orderNumber: insertOrder.orderNumber,
      driverId: insertOrder.driverId || null,
      pickupLocation: insertOrder.pickupLocation,
      deliveryLocation: insertOrder.deliveryLocation,
      status: insertOrder.status || "pending",
      estimatedTime: insertOrder.estimatedTime || null,
      distance: insertOrder.distance || null,
      fee: insertOrder.fee,
      photoUrl: insertOrder.photoUrl || null,
      createdAt: new Date(),
      completedAt: null,
    };
    this.orders.set(id, order);
    return order;
  }

  async updateOrder(id: string, updates: Partial<Order>): Promise<Order> {
    const existing = this.orders.get(id);
    if (!existing) throw new Error("Order not found");

    const updated = { ...existing, ...updates };
    if (updates.status === "delivered") {
      updated.completedAt = new Date();
    }
    this.orders.set(id, updated);
    return updated;
  }

  async acceptOrder(orderId: string, driverId: string): Promise<Order> {
    return this.updateOrder(orderId, { driverId, status: "accepted" });
  }

  async getEarningsByDriverId(driverId: string): Promise<Earning[]> {
    return Array.from(this.earnings.values()).filter(earning => earning.driverId === driverId);
  }

  async createEarning(insertEarning: InsertEarning): Promise<Earning> {
    const id = randomUUID();
    const earning: Earning = {
      ...insertEarning,
      id,
      date: new Date(),
    };
    this.earnings.set(id, earning);
    return earning;
  }

  async getDailyEarnings(driverId: string, date: string): Promise<Earning[]> {
    const targetDate = new Date(date);
    return Array.from(this.earnings.values()).filter(earning => 
      earning.driverId === driverId && 
      earning.date && earning.date.toDateString() === targetDate.toDateString()
    );
  }

  async getWeeklyEarnings(driverId: string): Promise<Earning[]> {
    const weekAgo = new Date();
    weekAgo.setDate(weekAgo.getDate() - 7);

    return Array.from(this.earnings.values()).filter(earning => 
      earning.driverId === driverId && 
      earning.date && earning.date >= weekAgo
    );
  }
}

export const storage = new MemStorage();

